"use strict";(self.webpackChunkprovenance_blockchain_developer_portal=self.webpackChunkprovenance_blockchain_developer_portal||[]).push([[8352],{67169:(e,t,n)=>{n.r(t),n.d(t,{assets:()=>c,contentTitle:()=>r,default:()=>h,frontMatter:()=>o,metadata:()=>s,toc:()=>l});var i=n(74848),a=n(28453);const o={},r="Onboard to Servicing",s={id:"learn/provenance-applications/loan-origination-system-los/loan-servicing",title:"Onboard to Servicing",description:"After the Figure loan is onboarded, funded, and validated, the loan is onboarded to the Figure Servicing system. Loans are onboarded to the servicing platform by jointly executing a multi-party BlockVault contract with Figure Servicing. The LOS provides the loan data to the contract and identifies which servicer it is using by specifying the servicing application\u2019s public key as the counter-party. The \u201cassign-to-servicer\u201d contract ensures that the requirements for servicing the loan are met. If the loan data is malformed in some way, the servicing application rejects the contract, and the onus is on the origination system to correct the issue.",source:"@site/docs/learn/provenance-applications/loan-origination-system-los/loan-servicing.md",sourceDirName:"learn/provenance-applications/loan-origination-system-los",slug:"/learn/provenance-applications/loan-origination-system-los/loan-servicing",permalink:"/docs/learn/provenance-applications/loan-origination-system-los/loan-servicing",draft:!1,unlisted:!1,tags:[],version:"current",frontMatter:{}},c={},l=[];function d(e){const t={code:"code",h1:"h1",header:"header",li:"li",ol:"ol",p:"p",pre:"pre",...(0,a.R)(),...e.components};return(0,i.jsxs)(i.Fragment,{children:[(0,i.jsx)(t.header,{children:(0,i.jsx)(t.h1,{id:"onboard-to-servicing",children:"Onboard to Servicing"})}),"\n",(0,i.jsx)(t.p,{children:"After the Figure loan is onboarded, funded, and validated, the loan is onboarded to the Figure Servicing system. Loans are onboarded to the servicing platform by jointly executing a multi-party BlockVault contract with Figure Servicing. The LOS provides the loan data to the contract and identifies which servicer it is using by specifying the servicing application\u2019s public key as the counter-party. The \u201cassign-to-servicer\u201d contract ensures that the requirements for servicing the loan are met. If the loan data is malformed in some way, the servicing application rejects the contract, and the onus is on the origination system to correct the issue."}),"\n",(0,i.jsx)(t.p,{children:"Partial example of an \u201cassign-to-servicing\u201d contract to onboard a loan into the servicing system:"}),"\n",(0,i.jsx)(t.pre,{children:(0,i.jsx)(t.code,{className:"language-kotlin",children:'@Participants([ORIGINATOR, SERVICER])\nopen class AssignLoanToServicer(\n        // ------------------------------------\n        // These are Cross-Scope Facts coming from the loan scope. They do not become facts in the scope\n        // in which this contract is executed.\n        // ------------------------------------\n        // Facts are alphabetical by fact name:\n        @Fact(LoanScopeFacts.additionalParties) private val additionalParties: LoanProtos.PartiesList,\n        @Fact(LoanScopeFacts.blockchainCustody) private val blockchainCustody: LoanProtos.BlockchainCustody,\n        @Fact(LoanScopeFacts.creditReports) private val creditReports: LoanProtos.CreditReportsList,\n        @Fact(LoanScopeFacts.digitalSignaturePackets) private val digitalSignaturePackets: DocumentProtos.DocumentWithDataList,\n        @Fact(LoanScopeFacts.documents) private val documents: DocumentProtos.DocumentList,\n        @Fact(LoanScopeFacts.funding) private val funding: LoanProtos.Funding,\n        @Fact(LoanScopeFacts.incomeRecords) private val incomeRecords: LoanProtos.IncomeRecordsList,\n        @Fact(LoanScopeFacts.loan) private val loan: LoanProtos.Loan,\n        @Fact(LoanScopeFacts.primaryParty) private val primaryParty: CustomerProtos.Party,\n        @Fact(LoanScopeFacts.servicing) private val servicing: io.provenance.proto.asset.LoanProtos.LoanServicing,\n        @Fact(LoanScopeFacts.signedPromNote) private val signedPromNote: DocumentProtos.Disclosure,\n        @Fact(LoanScopeFacts.triMergeReports) private val triMergeReports: LoanProtos.TriMergeReportsList,\n        @Fact(LoanScopeFacts.underwritingPacket) private val underwritingPacket: UnderwritingProtos.UnderwritingPacket\n) : P8eContract() {\n\n    @Function(SERVICER)\n    @Fact(LoanScopeFacts.servicingScopeId)\n    fun validateServicingRequirements(@Input(LoanScopeFacts.servicingScopeId) servicingScopeId: Util.UUID ) : Util.UUID = servicingScopeId.also {\n\n        // "loan" fact validation\n        val selectedOffer = underwritingPacket.selectedOffer\n        ValidationUtil.validateMoneyField(selectedOffer, selectedOffer.amount, "amount")?.also { failure(it) }\n        ValidationUtil.validateMoneyField(selectedOffer, selectedOffer.drawAmount, "drawAmount")?.let { failure(it) }\n        ValidationUtil.validateMoneyField(loan, loan.monthlyPaymentAmount, "monthlyPaymentAmount")?.also { failure(it) }\n        if (selectedOffer.amount.isValidSrv() && selectedOffer.drawAmount.isValidSrv()) {\n            val originationFeeAmount = view.originationFeeAmount\n            groupedRule("Derived origination fee should be at least zero, but was [amount (${view.loanAmount}) - drawAmount (${view.drawAmount}) = origination fee ($originationFeeAmount)]", {\n                originationFeeAmount gte BigDecimal.ZERO\n            }, ONBOARDING to FATAL, UPDATE to WARN)\n            if (originationFeeAmount gt BigDecimal.ZERO) {\n                rule("Loans with an origination fee should have an origination fee type, but had type [${view.originationFeeType}]") {\n                    selectedOffer.origFeeType.let { it.isNotBlank() && it != NO_FEE }\n                }\n            }\n        }\n\n        // "primary_party" fact validation\n        rule("Primary borrower was missing uuid") {\n            primaryParty.hasUuid()\n        }\n        rule("Primary borrower was missing name details. First Name: [${primaryParty.name.firstName}] | Last Name: [${primaryParty.name.lastName}]") {\n            primaryParty.hasName() && primaryParty.name.firstName.isNotBlank() && primaryParty.name.lastName.isNotBlank()\n        }\n\n        // "funding" fact validation\n        rule("Funding must have valid start/complete") { funding.completedDate.isValidSrv() && funding.startedDate.isValidSrv() }\n        rule("Funding must be set true") { funding.complete }\n        rule("Funding block must be non-default") { funding.isValidSrv() }\n        rule("Funding start is too early") { funding.startedDate.toCSTLocalDateSrv().isAfterInclusiveSrv(ValidationUtil.EARLIEST_DATE_ALLOWABLE.toLocalDate()) }\n        rule("Funding complete is too early") { funding.completedDate.toCSTLocalDateSrv().isAfterInclusiveSrv(ValidationUtil.EARLIEST_DATE_ALLOWABLE.toLocalDate()) }\n        rule("Funding complete cannot be in the future") { funding.completedDate.toOffsetDateTimeProv().isBeforeInclusiveSrv(ServicingTimeUtil.getServerTime()) }\n'})}),"\n",(0,i.jsx)(t.p,{children:"The contract execution to handoff a loan to the servicer has several unique characteristics:"}),"\n",(0,i.jsxs)(t.ol,{children:["\n",(0,i.jsxs)(t.li,{children:["The ",(0,i.jsx)(t.code,{children:"@Participants"})," annotation on the class indicates that two parties are required to execute this contract, one acting in the role of ORIGINATOR and the counter-party in the role of SERVICER."]}),"\n",(0,i.jsx)(t.li,{children:"This contract is executed in a unique scope, distinct from the loan scope."}),"\n",(0,i.jsxs)(t.li,{children:["The facts supplied in the contract constructor are pre-existing facts. However, the loan facts exist in the loan scope, not the new scope created for the servicing handoff. Nonetheless, the loan scope facts in their current state can be supplied as inputs to the ",(0,i.jsx)(t.code,{children:"AssignLoanToServicerContract"})," as cross-scope facts."]}),"\n",(0,i.jsx)(t.li,{children:"The ORIGINATOR initiates the contract execution and supplies the cross-scope loan facts."}),"\n",(0,i.jsx)(t.li,{children:"The SERVICER supplies one additional input, the UUID of the new scope the servicer establishes in order to store loan servicing data."}),"\n",(0,i.jsx)(t.li,{children:"If any of the servicing checks in this contract fail, they will throw an Exception and fail the contract execution."}),"\n",(0,i.jsxs)(t.li,{children:["Either party can terminate the contract execution at any time during the process. When the servicer receives the request to execute the contract, the servicing system can perform checks about whether it is willing to jointly execute the contract with the originator. The servicing system will check, for example, that the public key of the originator matches one or more of the keys expected by the set of originators handled by the system, or it might check that the ",(0,i.jsx)(t.code,{children:"loan.loanType"})," is one the system knows how to service."]}),"\n"]})]})}function h(e={}){const{wrapper:t}={...(0,a.R)(),...e.components};return t?(0,i.jsx)(t,{...e,children:(0,i.jsx)(d,{...e})}):d(e)}},28453:(e,t,n)=>{n.d(t,{R:()=>r,x:()=>s});var i=n(96540);const a={},o=i.createContext(a);function r(e){const t=i.useContext(o);return i.useMemo((function(){return"function"==typeof e?e(t):{...t,...e}}),[t,e])}function s(e){let t;return t=e.disableParentContext?"function"==typeof e.components?e.components(a):e.components||a:r(e.components),i.createElement(o.Provider,{value:t},e.children)}}}]);